import React, { Component } from 'react'
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input,
    ListGroup,
    ListGroupItem
} from 'reactstrap'

import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { getItems } from '../actions/ItemActions'

class AddQuoteItem extends Component {

    componentDidMount() {
        this.props.getItems()
        this.props.toEstimate('')
    }

    state = {
        modal: false,
        items: [],
        toEstimate: ''
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        })
    }

    onListClick = (name, price) => {
        console.log("click " + name + ", " + price)
        this.props.toEstimate(name)
        this.toggle()
    }

    render() {
        const { items } = this.props.item

        return (
            <div>
                <Button
                    color="dark"
                    style={{marginBottom: '2rem'}}
                    onClick={this.toggle}
                >Add quote item</Button>
                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                    >
                        <ModalHeader
                            toggle={this.toggle}
                        >Pick a material</ModalHeader>
                        <ModalBody>
                        <ListGroup>
                            {items.map(({ _id, name, measured_by, price, category }) => (
                            <ListGroupItem 
                            key={_id}
                            onClick={ this.onListClick.bind (this, name, price) }>
                                {name}, {measured_by}, {price}, {category}
                            </ListGroupItem>
                        ))}
                        </ListGroup>
                        </ModalBody>
                </Modal>
            </div>
        )
    }
}

AddQuoteItem.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    item: state.item
})

export default connect(mapStateToProps, { getItems })(AddQuoteItem)
