import React, { Component } from 'react'
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input
} from 'reactstrap'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { getItems } from '../actions/ItemActions'

class AddQuoteItem extends Component {

    componentDidMount() {
        this.props.getItems()
    }

    state = {
        modal: false,
        items: []
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        })
    }

    render() {
        const { items } = this.props.item

        return (
            <div>
                <Button
                    color="dark"
                    style={{marginBottom: '2rem'}}
                    onClick={this.toggle}
                >Add quote item</Button>
                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                    >
                        <ModalHeader
                            toggle={this.toggle}
                        >Pick a material</ModalHeader>
                        <ModalBody>
                        <ul>
                            {items.map(({ _id, name, measured_by, price, category }) => (
                            <li key={_id} timeout={500} classNames="fade">
                            <Button>
                                {name}, {measured_by}, {price}, {category}
                                </Button>
                            </li>
                        ))}
                        </ul>
                        </ModalBody>
                </Modal>
            </div>
        )
    }
}

AddQuoteItem.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    item: state.item
})

export default connect(mapStateToProps, { getItems })(AddQuoteItem)
