export let timesClicked = 0;
export default function sketch (p) {

    p.setup = function () {
        p.createCanvas(300, 300);
    };

    p.draw = function () {
        p.background(255)
        p.image(p.graphic, 0, 0)
    
        p.tool0Preview()

        if (p.mouseIsPressed) {
            p.drawOnGraphic()
        } else {
            p.lastPoint = null
            p.onMouseQuit()
        }
    };

    function tool0Preview() {
    p.noFill()
    p.stroke(90)
    p.strokeWeight(1)

    let destX = (Math.floor(p.mouseX / 40)) * 40
    let destY = (Math.floor(p.mouseY / 40)) * 40

    p.ellipse(destX, destY, 4, 4) }

