import { GET_ITEMS, ADD_ITEM, DELETE_ITEM } from './types'

export const getItems = () => {
    console.log("get called")
    return {
        type: GET_ITEMS
    }
}

export const deleteItem = () => {
    console.log("delete called")
    return {
        type: DELETE_ITEM,
        // payload: id
    }
}
