import React, { Component } from 'react'
import { 
    Container, 
    ListGroup, 
    ListGroupItem, 
    Button 
} from 'reactstrap'
import {
    CSSTransition,
    TransitionGroup
} from 'react-transition-group'
import uuid from 'uuid'
import { connect } from 'react-redux'
import { getItems } from '../actions/ItemActions'

class MaterialList extends Component {
    
    render() {
        const { items } = this.state
        return (
            <Container>
                <Button 
                color="dark"
                style={{marginBottom: '2rem'}}
                onClick={() => {
                    const name = prompt("Enter material's name")
                    const measured_by = prompt('Measured by')
                    const price = prompt('Price per unit')
                    const category = prompt('Material Category')
                    if(name && measured_by && price && category) {
                        this.setState(state => ({
                            items: [...state.items, { id: uuid(), name, measured_by, price, category}]
                        }))
                    }
                }}
                >Add material
                </Button>

                <ListGroup>
                    <TransitionGroup>
                        {items.map(({ id, name, measured_by, price, category }) => (
                            <CSSTransition key={id} timeout={500} classNames="fade">
                                <ListGroupItem>
                                <Button
                                    className="remove-btn"
                                    color="danger"
                                    size="sm"
                                    onClick={() => {
                                        this.setState(state => ({
                                            items: state.items.filter(item => item.id !==id)
                                        }))
                                    }}
                                >&times;</Button>
                                    {name}, {measured_by}, {price}, {category}
                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        )
    }
}

export default connect()(MaterialList)
