export let timesClicked = 0;
export default function sketch (p) {

    p.setup = function () {
        p.createCanvas(300, 300);
    };

    p.draw = function () {
        p.background(255)
        p.image(graphic, 0, 0)
    
        p.tool0Preview()

        if (p.mouseIsPressed) {
            p.drawOnGraphic()
        } else {
            lastPoint = null
            p.onMouseQuit()
        }
    };

    function tool0Preview() {
    noFill()
    stroke(90)
    strokeWeight(1)

    let destX = (Math.floor(mouseX / grid)) * grid
    let destY = (Math.floor(mouseY / grid)) * grid

    ellipse(destX, destY, radius, radius) }

