import React, { Component } from 'react'
import AppNavbar from '../components/AppNavbar'
import AddQuoteItem from '../components/AddQuoteItem'

import { 
    Container, 
    ListGroup, 
    ListGroupItem 
} from 'reactstrap'

import { Provider } from 'react-redux'
import store from '../store'

import 'bootstrap/dist/css/bootstrap.min.css'
import '../App.css';

class EstimatePage extends Component {

    state = {
        quotedItems: ['1', '2', '3']
    }

    getQuoteItem = (name) => {
        this.state.quotedItems.push(name)
        console.log(this.state.quotedItems)
    }

    quotedItemsList() {
        return this.state.quotedItems.map(( item ) => {
            return (
                    <ListGroupItem 
                        key={item._id}>
                        {item}
                    </ListGroupItem>
                )
            })
        }

    render() {
        return (
            <Provider store={store}>
            <div>
                <AppNavbar/>
                <Container>
                    <h1>Estimate you house cost</h1>
                    <br></br>
                    <h4>Estimated total cost : 0.00 $</h4>
                    <br></br>
                    <AddQuoteItem toEstimate = { this.getQuoteItem }/>
                    <ListGroup>
                        {this.quotedItemsList}
                    </ListGroup>
                </Container>
            </div>
            </Provider>
        )
    }
}

